jQuery(document).ready(function() {
  
  var $navbar = jQuery('#primary-menu');
  var $hamburger = jQuery('.hamburger-menu');
  var $window = jQuery(widnow);

  function adjustHamburger() {
    if ($window.width() > 992) $navbar.show();
    if ($window.width() <= 992 && jQuery('.bar.animate').length == 0) $navbar.hide();
  }

  $hamburger.on('click', function () {
    $navbar.toggleClass('show', 1000);
    jQuery('.bar').toggleClass('animate');
  });

  $window.resize(function () {
    adjustHamburger();
  });

});